// CONFIGURATION
/**
 * @param {string} folder - A string of folder names, separated by '/' (slashes)
 * @param {number} defaultPermission - A constant number, to be gathered from the global object CONST.DOCUMENT_PERMISSION_LEVELS (for quick reference, you can replace 'OBSERVER' with one of the following: 'NONE', 'LIMITED' or 'OWNER'
 * @param {string} sheetType - Change this if you have a module installed for a different loot sheet type.
 */
const c = {
  folder: "Misc/Loot",
  defaultPermission: CONST.DOCUMENT_PERMISSION_LEVELS.OBSERVER,
  sheetType: "pf1.ActorSheetPFNPCLoot",
};

// END CONFIGURATION

async function createFromTokens(tokens) {
  let lastFolder = null;

  async function createFolders() {
    let lastIndex = 0;
    const folders = game.folders.filter(f => f.type === "Actor");
    const folderNames = c.folder.split("/");
    for (let a = 0; a < folderNames.length; a++) {
      const curName = folderNames[a];
      const prevName = a > 0 ? folderNames[a - 1] : null;

      const f = folders.find(o => o.name === curName && (o.parent ? o.parent.name === prevName : true));
      if (f) {
        lastFolder = f;
        lastIndex = a + 1;
      }
    }

    for (let a = lastIndex; a < folderNames.length; a++) {
      const newFolder = await Folder.create({
        name: folderNames[a],
        parent: lastFolder?.id ?? null,
        type: "Actor",
      });
      lastFolder = newFolder;
    }
  }

  // Create folder(s)
  if (c.folder) await createFolders();

  // Gather currency
  const currency = { pp: 0, gp: 0, sp: 0, cp: 0 };
  tokens.forEach(t => {
    for (const cc of Object.keys(currency))
      currency[cc] += t.actor.system.currency[cc];
  });

  // Create loot NPC for selected tokens
  const name = "(Loot) " + tokens.map(t => t.name).join(", ");

  const lootNPC = await Actor.create({
    name,
    type: "npc",
    img: tokens[0].actor.img,
    folder: lastFolder?.id ?? null,
    permission: {
      default: c.defaultPermission,
    },
    flags: {
      core: { sheetClass: c.sheetType },
    },
    system: { currency },
  });

  const scrubItem = (i) => {
    delete i.system.links; // Scrap links
    delete i._id; // scrap ID
    delete i.system.tag; // scrap tag
    return i;
  }

  // Gather item data
  const itemData = tokens.reduce((cur, o) => {
    for (const i of o.actor.items) {
      const itemData = i.toObject();
      if (!i.isPhysical || i.system.quantity <= 0) continue;
      scrubItem(itemData);
      // Check for identical data
      const oldItem = cur.find(i2 => i2.name === i.name && i2.type === i.type);
      let sameItem = false;
      if (oldItem) {
        // Ensure the objects are similar enough (quantity is the only thing allowed to differ)
        const oldKeys = Object.keys(flattenObject(diffObject(itemData, oldItem)));
        sameItem = (oldKeys.length == 1 && oldKeys.includes("quantity")) || oldKeys.length == 0;
      }

      if (oldItem && sameItem) oldItem.system.quantity += i.system.quantity;
      else cur.push(itemData);
    }
    return cur;
  }, []);

  // Add items
  await lootNPC.createEmbeddedDocuments("Item", itemData);
}

// Get selected tokens
const tokens = canvas.tokens.controlled;
if (tokens.length) createFromTokens(tokens);
else ui.notifications?.warn("No tokens selected to convert into loot.");
